import React, { useState } from 'react';
// Added Apple icon for the new card
import { Apple } from 'lucide-react'; // Make sure lucide-react is installed

// Enhanced card component (modified to take cardId)
const DashboardCard = ({ title, imageUrl, onSelect, colorClass, delay, cardId }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      // Use cardId for the selection callback
      onClick={() => onSelect(cardId)}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      className={`relative ${colorClass} text-white rounded-2xl shadow-xl p-4 sm:p-6 flex flex-col items-center justify-center
                   transform transition-all duration-300 hover:scale-105 cursor-pointer
                   overflow-hidden animate-bounce-in aspect-square`} // Added aspect-square for consistency
      style={{ animationDelay: `${delay}ms` }}
      role="button" // Accessibility
      tabIndex={0} // Accessibility
      onKeyDown={(e) => e.key === 'Enter' && onSelect(cardId)} // Accessibility
      aria-label={`Learn about ${title}`} // Accessibility
    >
      {/* Sparkle effect on hover */}
      {isHovered && (
        <div className="absolute inset-0 pointer-events-none z-10"> {/* Ensure sparkles are above image */}
          <div className="sparkle sparkle-1">‚ú®</div>
          <div className="sparkle sparkle-2">‚≠ê</div>
          <div className="sparkle sparkle-3">üí´</div>
        </div>
      )}

    
      {/* Use a placeholder image generator */}
      <img
        src={imageUrl} // Use the passed imageUrl
        alt={`${title} icon`}
        className={`w-16 h-16 sm:w-20 sm:h-20 md:w-24 md:h-24 object-contain mb-2 sm:mb-3 filter drop-shadow-lg transition-transform duration-300 ${isHovered ? 'scale-110 rotate-6' : ''}`} // Slightly smaller scale on hover
        loading="lazy" // Improve performance
      />
      <h3 className="text-base sm:text-lg font-extrabold z-10 text-center"> {/* Removed animate-pulse */}
        {title}
      </h3>
    </div>
  );
};

